    const isSupported = function (currency: string) {
      try {
        const nf = new Intl.NumberFormat('en', {
          style: 'currency',
          currencyDisplay: 'name',
          currency,
        });
        const fmt = nf.format(123);
        if (fmt.substring(0, 3) != currency && fmt.substring(fmt.length - 3) != currency) {
          return true;
        }
      } catch (e) {
        // cause exception
      }
      return false;
    };

    const r: string[] = [];
    CURRENCIES_IN_CLDR.forEach(function (item) {
      if (item.length == 3) {
        if (isSupported(item)) {
          r.push(item);
        }
      } else if (item.length == 5 && item.indexOf('~') == 3) {
        const start = ATOZ.indexOf(item[2]);
        const end = ATOZ.indexOf(item[4]);
        for (let i = start; i <= end; i++) {
          const c = item.substring(0, 2) + ATOZ[i];
          if (isSupported(c)) {
            r.push(c);
          }
        }
      }
    });
    return r;
